import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-lista-juegos',
  templateUrl: './lista-juegos.page.html',
  styleUrls: ['./lista-juegos.page.scss'],
  standalone: false,
})
export class ListaJuegosPage implements OnInit {

  juegos = [
    {
      nombre: 'Among Us',
      desarrollador: 'Innersloth',
      imagen: 'data:image/jpeg;base64,',
    },
    {
      nombre: 'Fifa 23',
      desarrollador: 'EA Sports',
      imagen: 'data:image/jpeg;base64,',
    },
    {
      nombre: 'Minecraft: Java & Bedrock',
      desarrollador: 'Microsoft Studios',
      imagen: 'https://preview.redd.it/new-minecraft-art-cover-what-do-you-think-v0-jncj9eqr9bad1.jpeg?auto=webp&s=8ba0359485474ee23ca522d0fdddb59bdec9c250',
    },
    {
      nombre: 'Halo: The Master Chief Collection',
      desarrollador: 'Xbox Game Studios',
      imagen: 'data:image/jpeg;base64,',
    },
    {
      nombre: 'Age of Empires II: Definitive Edition',
      desarrollador: 'Xbox Game Studios',
      imagen: 'data:image/jpeg;base64,',
    },
    {
      nombre: 'Halo Reach',
      desarrollador: 'Xbox Game Studios',
      imagen: 'data:image/jpeg;base64,',
    },
  ];

  mostrarFormulario = false;

  nuevoJuego = {
    nombre: '',
    desarrollador: '',
    imagen: '',
  };

  toggleFormulario() {
    this.mostrarFormulario = !this.mostrarFormulario;
  }

  agregarJuego() {
    if (
      this.nuevoJuego.nombre.trim() !== '' &&
      this.nuevoJuego.desarrollador.trim() !== '' &&
      this.nuevoJuego.imagen.trim() !== ''
    ) {
      this.juegos.push({ ...this.nuevoJuego });

      // Reiniciar formulario
      this.nuevoJuego = { nombre: '', desarrollador: '', imagen: '' };
      this.mostrarFormulario = false;
    }
  }

  eliminarJuego(indice: number) {
    this.juegos.splice(indice, 1);
  }

  actualizarJuego(indice: number) {
    const juego = this.juegos[indice];
    this.nuevoJuego = { ...juego };
    this.mostrarFormulario = true;
    this.juegos.splice(indice, 1); // Eliminar el juego para luego agregarlo actualizado
  }

  constructor() { }

  ngOnInit() {
  }
}
